figur =  [("T1", "ACATAA"), ("T2", "AAGTCA"), ("T3", "ACGTGC"), ("T4", "AAGTTC"), ("T5", "ACGTAA")]

-- Snabbtest vid redovisningar
t1 = seqdata figur
snabbtest de l = de s1 s2
   where sl = l
         s1 = sl !! 0
         s2 = sl !! 1

